var setSize = function() {
  var copie = $('.tag-list').clone();
  copie.addClass('copy').insertAfter('.tag-list');
  $('.copy')
    .children()
    .removeClass('filter');
  $('.copy')
    .children()
    .show('3000');
  $('.copy').css('height', 'auto');
  var height = $('.copy').height();
  var height = $('.copy').height();
  $('.copy').remove();
  if (height < $(window).height()) {
    $('.tag-list').height(height);
  } else {
    setTimeout(function() {
      $('.tag-list').css('height', 'auto');
    }, 300);
  }
};
setSize();
$(window).resize(setSize);

$('.filter-button').click(function() {
  var height = $('.tag-list').height();
  var height = $('.tag-list').height(height);
  var value = $(this).attr('data-filter');
  $('.filter')
    .not('.' + value)
    .hide('3000');
  $('.filter')
    .filter('.' + value)
    .show('3000');
  setSize();
});

